import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Peea18chukService } from '../../services/peea18chuk.service';
import { ContratoService } from '../../services/contrato.service';
import { RepresentanteService } from '../../services/representante.service';
import { EstudianteService } from '../../services/estudiante.service';

import pdfMake from 'pdfmake/build/pdfmake';
import pdfFonts from 'pdfmake/build/vfs_fonts';
import { Peea18chuk } from '../peea18chuk.model';
pdfMake.vfs = pdfFonts.pdfMake.vfs;

@Component({
  selector: 'app-reporte-peea18charlotte',
  templateUrl: './reporte-peea18charlotte.component.html',
  styles: [
  ]
})
export class ReportePeea18charlotteComponent implements OnInit {

  public peea18: Peea18chuk;
  public contrato: any;
  public representante: any;
  public estudiante: any;

  constructor(
    private activatedRoute: ActivatedRoute,
    private router: Router,
    private peeaService: Peea18chukService,
    private contratoSevice: ContratoService,
    private representanteService: RepresentanteService,
    private estudianteService: EstudianteService
  ) { }

  ngOnInit(): void {
    this.activatedRoute.params.subscribe(({ id }) => {
      this.cargarDatos(id);
    });
  }

  calcularEdad(fecha) {
    var hoy = new Date();
    var cumpleanos = new Date(fecha);
    var edad = hoy.getFullYear() - cumpleanos.getFullYear();
    var m = hoy.getMonth() - cumpleanos.getMonth();

    if (m < 0 || (m === 0 && hoy.getDate() < cumpleanos.getDate())) {
      edad--;
    }

    return edad;
  }

  cargarDatos(idPeea: any) {
    this.peeaService.obtenerPeea18chukById(idPeea).subscribe((resp: any) => {
      this.peea18 = resp.data;
      this.contratoSevice.obtenerContratoById(this.peea18?.idContrato).subscribe((resp: any) => {
        this.contrato = resp.data;
        this.representanteService.obtenerRepresentanteById(resp.data.idRepresentante).subscribe((resp: any) => {
          this.representante = resp.data;
          this.estudianteService.obtenerEstudianteById(this.peea18?.idEstudiante).subscribe((resp: any) => {
            this.estudiante = resp.data;
            this.createPDF(this.estudiante, this.representante, this.contrato, this.peea18);
          });
        });
      });
    })
  }

  createPDF(estudiante: any, representante: any, contrato: any, peea: any) {

    let arrayDeParentesco = [];
    peea.pregunta5.forEach(element => {
      arrayDeParentesco.push([element.parentesco, element.nombre, element.edad, element.ocupacion]);
    });

    const pdfDefinition: any = {

      content: [
        {
          image:'data:image/png;base64,',
          width: 200,
          alignment: 'center',
        },
        '\n\n',
        {
          text: [
            { text: 'Estimado padre de familia, le recordamos que esta información es de carácter confidencial y que todo lo que usted escriba aquí, se manejará de forma ética y profesional ya que nos servirá como apoyo en las necesidades que se requieran en la capacitación de su representado.', style: 'header', bold: true, fontSize: 12, color: 'black' },
            `${estudiante?.nombresApellidos}`,
          ]
        },
        '\n\n',
        {
          text: 'DATOS PERSONALES DEL ESTUDIANTE',
          style: 'header',
          alignment: 'center',
          color: '#E84B20',
          bold: true
        },
        '\n\n',
        {
          text: [
            { text: 'Nombre: ', style: 'header', bold: true, fontSize: 12, color: 'black' },
            `${estudiante?.nombresApellidos}`,
          ]
        },
        '\n',
        {
          columns: [
            
            {
              text: [
                { text: 'Edad: ', style: 'header', bold: true, fontSize: 12 },
                `${this.calcularEdad(estudiante?.fechaNacimiento)}`,
              ]
            },
            {
              text: [
                { text: 'Fecha de Nacimiento: ', style: 'header', bold: true, fontSize: 12 },
                `${estudiante?.fechaNacimiento}`,
              ]
            },
          ]
        },
        '\n',
        {
          text: [
            { text: 'Dirección: ', style: 'header', bold: true, fontSize: 12, color: 'black' },
            `${representante?.direccion}`,
          ]
        },
        '\n',
        {
          columns: [
            
            {
              text: [
                { text: 'Lugar de nacimiento: ', style: 'header', bold: true, fontSize: 12 },
                `${peea?.pregunta1}`,
              ]
            },
            {
              text: [
                { text: 'Redes sociales (estudiante - representante) : ', style: 'header', bold: true, fontSize: 12 },
                `${peea?.peea?.pregunta2}`,
              ]
            },
          ]
        },
        '\n\n',
        {
          text: '1.- HISTORIA FAMILIAR Y PERSONAL',
          style: 'header',
          color: '#E84B20',
          bold: true
        },
        '\n\n',
        {
          style: 'tableExample',
          table: {
            body: [
              ['PARENTESCO', 'NOMBRE', 'EDAD', 'OCUPACIÓN'],
              ...arrayDeParentesco
            ]
          }
        },
        '\n',
        {
          text: [
            { text: 'Estado civil de los padres: ', style: 'header', bold: true, fontSize: 12, color: 'black' },
            `${peea?.pregunta6}`,
          ]
        },
        '\n',
        {
          text: [
            { text: 'Con quien prectica usted ingles: ', style: 'header', bold: true, fontSize: 12, color: 'black' },
            `${peea?.pregunta7}`,
          ]
        },
        '\n\n',
        {
          text: '2.- SALUD',
          style: 'header',
          color: '#E84B20',
          bold: true
        },
        '\n\n',
        {
          style: 'tableExample',
          table: {
            body: [
              ['PREGUNTA', 'RESPUESTA', 'OBSERVACIONES'],
              ['¿Tiene alguna dificultad visual? ', `${peea?.pregunta8?.respuesta}`, `${peea?.pregunta8?.observacion}`],
              ['¿Se ha practicado alguna vez exámenes auditivos? ', `${peea?.pregunta9?.respuesta}`, `${peea?.pregunta9?.observacion}`],
              ['¿Se ha practicado alguna vez exámenes visuales? ', `${peea?.pregunta10?.respuesta}`, `${peea?.pregunta10?.observacion}`],
                       
            ]
          }
        },
        '\n\n',
        {
          text: '3.- LABORAL',
          style: 'header',
          color: '#E84B20',
          bold: true
        },
        '\n\n',
        {
          style: 'tableExample',
          table: {
            body: [
              ['PREGUNTA', 'RESPUESTA'],
              ['Principales funciones ', `${peea?.pregunta11}`],
              ['Jornada Laboral (Especificar Horarios) ', `${peea?.pregunta12}`],
            ]
          }
        },
        '\n\n',
        {
          text: '4.- ACUERDOS',
          style: 'header',
          color: '#E84B20',
          bold: true
        },
        '\n\n',
        {
          text: [
            { text: 'Mencione los acuerdos establecidos con su Consultor Educativo', style: 'header', bold: true, fontSize: 12, color: 'black' },
            `${peea?.pregunta13}`,
          ]
        },

      ]

    }

    const pdf = pdfMake.createPdf(pdfDefinition);
    pdf.open();

  }


}
